<?phpclass Question {    var $id;    var $title;    var $tags;    var $body;    public function __construct($id, $title, $tags, $body) {        $this->id = $id;        $this->title = $title;        $this->tags = $this->readTags($tags);        $this->body = $body;    }    public function display_short() {        echo "<h2><a href=index.php?task=viewquestion&id=" . $this->id . ">" . $this->title . "</a></h2>";        $this->viewTags();//        echo $this->body;    }    public function display_full() {        echo "<h2><a href=index.php?task=viewquestion&id=" . $this->id . ">" . $this->title . "</a></h2>";        $this->viewTags();        echo $this->body;    }    public function readTags($tag) {        // Use string function to read here        $strCut = substr(substr($tag, 1), 0, -1);        return explode("><", $strCut);    }        public function viewTags(){        echo "Tags: ";        foreach($this->tags as $tag){            echo "||"."<a href=http://google.com.vn>".$tag."</a>";        }    }}?>