<?phpclass Question {    var $id;    var $title;    var $tags;    var $body;    public function _construct($id, $title, $tags, $body) {        $this->id = $id;        $this->title = $title;        $this->tags = $readTags($tags);        $this->body = $body;    }    public function display() {        echo "<h2><a href=index.php?task=view&id=" . $this->id . ">" . $this->title . "</a></h2>";        print_r($this->tags);        echo $this->body;    }    public function readTags($tag) {        // Use string function to read here        $strCut = substr(substr($tag, 1), 0, -1);        return explode("><", $strCut);    }}?>